package com.hegazy.mushafy;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentTransaction;public interface Const {    public static final String PAGE_NUMBER = "page_number";    public static final String AUTHOR = "author";    public static final String SURAH = "surah";    public static final String ACTION = "action";    public static final String SAVED = "saved";    public static void replaceFragment(FragmentManager getChildFragmentManager, int id, Fragment fragment) {        FragmentTransaction transaction = getChildFragmentManager.beginTransaction();        transaction.replace(id, fragment);        transaction.addToBackStack(null);        transaction.commit();    }}